<node CREATED="1620303266538" ID="ID_177464054" MODIFIED="1620303268885" TEXT="Nutzen"/>
<node CREATED="1620303269772" ID="ID_1640446361" MODIFIED="1620303291514" TEXT="Risiko"/>
<node CREATED="1620303356824" FOLDED="true" ID="ID_411983138" MODIFIED="1620306233074" TEXT="Grafik 6 Eck">
